mapper.sql:
        
    		t.is_deleted = '0'
			
				AND t.id = #{params.id}
			
			
				AND t.parent_id = #{params.parentId}
			
			
				AND t.order_num = #{params.orderNum}
			
			
				AND t.menu_name = #{params.menuName}
			
			
				AND t.url = #{params.url}
			
			
				AND t.open_type = #{params.openType}
			
			
				AND t.menu_type = #{params.menuType}
			
			
				AND t.perms = #{params.perms}
			
			
				AND t.icon = #{params.icon}
			
			
				AND t.del_ind = #{params.delInd}
			
			
				AND t.create_user = #{params.createUser}
			
			
				AND t.create_time = #{params.createTime}
			
			
				AND t.update_user = #{params.updateUser}
			
			
				AND t.update_time = #{params.updateTime}
			
			
				AND t.version = #{params.version}
			
        
        ORDER BY t.create_time DESC
